import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { Formik, Form, Field, ErrorMessage } from "formik";
import * as Yup from "yup";

function OrderForm() {
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  // Initial values for the form
  const initialValues = {
    product_id: "",
    quantity: "",
    status: "Pending",
  };

  // Validation schema using Yup
  const validationSchema = Yup.object({
    product_id: Yup.number()
      .required("Product ID is required")
      .positive("Product ID must be a positive number"),
    quantity: Yup.number()
      .required("Quantity is required")
      .positive("Quantity must be a positive number"),
    status: Yup.string().required("Status is required"),
  });

  // Submit function to handle the form submission
  const handleSubmit = (values, { setSubmitting }) => {
    setError(null);
    const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5000";

    // Make POST request to the backend API
    axios
      .post(`${API_URL}/orders`, values)
      .then((response) => {
        // Navigate to the orders list page after successful submission
        navigate("/orders");
      })
      .catch((error) => {
        // Handle errors and display error messages
        const errorMessage =
          error.response?.data?.error || error.message || "Something went wrong!";
        setError(errorMessage);
        setSubmitting(false);
      });
  };

  // Render the form
  return (
    <div>
      <h2>Add Order</h2>
      {error && <div className="error">{error}</div>}
      <Formik
        initialValues={initialValues}
        onSubmit={handleSubmit}
        validationSchema={validationSchema}
      >
        {({ isSubmitting }) => (
          <Form>
            <div>
              <Field
                type="number"
                name="product_id"
                placeholder="Product ID"
                min="1"
              />
              <ErrorMessage name="product_id" component="div" className="error" />
            </div>
            <div>
              <Field
                type="number"
                name="quantity"
                placeholder="Quantity"
                min="1"
              />
              <ErrorMessage name="quantity" component="div" className="error" />
            </div>
            <div>
              <Field as="select" name="status" defaultValue="Pending">
                <option value="Pending" disabled>
                  Pending
                </option>
                <option value="Shipped">Shipped</option>
                <option value="Delivered">Delivered</option>
              </Field>
              <ErrorMessage name="status" component="div" className="error" />
            </div>
            <button type="submit" disabled={isSubmitting}>
              {isSubmitting ? "Submitting..." : "Add Order"}
            </button>
          </Form>
        )}
      </Formik>
    </div>
  );
}

export default OrderForm;
