import React, { useState, useEffect } from "react";
import axios from "axios";

function SupplierList() {
  const [suppliers, setSuppliers] = useState([]);

  useEffect(() => {
    axios
      .get("http://127.0.0.1:5000/suppliers/")
      .then((response) => {
        console.log("Response data:", response.data); // Log API response
        // Safeguard: Ensure the response is an array
        setSuppliers(Array.isArray(response.data) ? response.data : []);
      })
      .catch((error) => {
        console.error("Error fetching suppliers:", error);
      });
  }, []);

  return (
    <div>
      <h2>Supplier List</h2>
      <ul>
        {/* Safeguard: Check if `suppliers` is an array before mapping */}
        {Array.isArray(suppliers) && suppliers.length > 0 ? (
          suppliers.map((supplier) => (
            <li key={supplier.id}>
              {supplier.address} - {supplier.contact_info}
            </li>
          ))
        ) : (
          <li>No suppliers available</li>
        )}
      </ul>
    </div>
  );
}

export default SupplierList;
