import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { Formik, Form, Field, ErrorMessage } from "formik";
import * as Yup from "yup";

function OrderForm() {
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const initialValues = {
    product_id: "",
    quantity: "",
    status: "Pending"
  };

  // Validation schema with Yup
  const validationSchema = Yup.object({
    product_id: Yup.number().required("Product ID is required").positive("Product ID must be a positive number"),
    quantity: Yup.number().required("Quantity is required").positive("Quantity must be a positive number"),
    status: Yup.string().required("Status is required")
  });

  const handleSubmit = (values) => {
    setError(null);  // Clear any previous error message
    axios.post("http://localhost:5000/orders", values)
      .then(() => {
        navigate("/orders");
      })
      .catch((error) => {
        // Check if error.response or error.response.data is undefined
        const errorMessage = error.response?.data?.error || "Something went wrong, please try again!";
        setError(errorMessage);
      });
  };

  return (
    <div>
      <h2>Add Order</h2>
      {error && <div className="error">{error}</div>}
      <Formik
        initialValues={initialValues}
        onSubmit={handleSubmit}
        validationSchema={validationSchema}
      >
        <Form>
          <div>
            <Field type="number" name="product_id" placeholder="Product ID" required />
            <ErrorMessage name="product_id" component="div" className="error" />
          </div>
          <div>
            <Field type="number" name="quantity" placeholder="Quantity" required />
            <ErrorMessage name="quantity" component="div" className="error" />
          </div>
          <div>
            <Field as="select" name="status">
              <option value="Pending">Pending</option>
              <option value="Shipped">Shipped</option>
              <option value="Delivered">Delivered</option>
            </Field>
            <ErrorMessage name="status" component="div" className="error" />
          </div>
          <button type="submit">Add Order</button>
        </Form>
      </Formik>
    </div>
  );
}

export default OrderForm;
